mapper.select:
	SELECT * FROM (
		SELECT Tb.*, rownum rNum FROM (
			SELECT seq, title, img, description, register_date
			FROM press
			WHERE 1=1
			ORDER BY
                seq DESC
		) Tb
	)
	WHERE rNum >= #{start}
	AND rNum <= #{end}

	SELECT * FROM (
		SELECT Tb.*, rownum rNum FROM (
			SELECT seq, title, img, description, register_date
			FROM press
			WHERE 1=1
			
				AND (
					
                       	title LIKE '%' || #{keyword} || '%'
                       	OR description LIKE '%' || #{keyword} || '%'
                   	
				)
			
			ORDER BY
                seq DESC
		) Tb
	)
	WHERE rNum >= #{start}
	AND rNum <= #{end}
